mapper.insert:
INSERT INTO tour_checklist
SET tour_id = #{tourId}
, checklist_id = #{checklistId}
, title = #{title}

mapper.update:
UPDATE tour_checklist
SET title = #{title}
WHERE checklist_id = #{checklistId}

mapper.select:
SELECT checklist_id AS checklistId, title, checked
FROM tour_checklist
WHERE tour_id = #{tourId}
